// Set Font By String#include <Fonts.h>#include <TextUtils.h>#ifndef __CONDITIONALMACROS__#include "ConditionalMacros.h"#endif // __CONDITIONALMACROS__#if TARGET_OS_WIN32	#ifndef __QUICKTIMEVR__	#include <QuickTimeVR.h>	#endif	#ifndef __QTUtilities__	#include "QTUtilities.h"	#endif	#ifndef __QTVRUtilities__	#include "QTVRUtilities.h"	#endif	#include <TextUtils.h>	#include <Script.h>	#include <string.h>#endif // TARGET_OS_WIN32#include "Resources.h"#include "Set Font By String.h"short GetFontNumByString( StringPtr s){	short		fontNum;#if TARGET_OS_MAC	if (s[0] == 0) return 0;	if (( s[0] == 1) && ( s[1] == '0')) return 0;	if (( s[0] == 1) && ( s[1] == '1')) return 1;		GetFNum( s, &fontNum);	return( fontNum);#else	return( 0);#endif TARGET_OS_MAC}void SetFontByString( StringPtr s){	short		fontNum;#if TARGET_OS_MAC	if (s[0] == 0) return;	GetFNum( s, &fontNum);	TextFont( fontNum);#else	TextFont( 0);#endif TARGET_OS_MAC}void SetFontByIndString( short resID, short num){	Str255		s;	short		fontNum;#if TARGET_OS_MAC	GetIndString( s, resID, num);	if (s[0] == 0) return;	GetFNum( s, &fontNum);	TextFont( fontNum);#else	TextFont( 0);#endif	}void SetFontAndSizeByIndString( short resID, short num){	Str255		s;	short		fontNum;	long		fontSize;		GetIndString( s, resID, num);	if (s[0] == 0) return;#if TARGET_OS_MAC	GetFNum( s, &fontNum);#else	fontNum = 0;#endif	TextFont( fontNum);	GetIndString( s, resID, num + 1);	if (s[0] == 0) return;	StringToNum( s, &fontSize);	TextSize( fontSize);	}short GetFontNumByIndString( short resID, short num){	Str255		s;	short		fontNum;	GetIndString( s, resID, num);	if (s[0] == 0) return 0;	// system font?#if TARGET_OS_MAC	fontNum = GetFontNumByString( s);#else	fontNum = 0;#endif TARGET_OS_MAC		return( fontNum);}